import React from 'react'
const Header = ({name}) => {
    return (
        <div>
            <h1>{name}</h1>
        </div>
    )
}

const Content = (props) => {
    return (
        <div>
            <Part1 part1={props.parts[0].name} exercises1={props.parts[0].exercises} />
            <Part2 part2={props.parts[1].name} exercises2={props.parts[1].exercises} />
            <Part3 part3={props.parts[2].name} exercises3={props.parts[2].exercises} />
        </div>
    )
}

const Part1 = (props) => {
    return (
        <p>
        {props.part1} {props.exercises1}
        </p>
    )
}

const Part2 = (props) => {
    return (
        <p>
        {props.part2} {props.exercises2}
        </p>
    )
}

const Part3 = (props) => {
    return (
        <p>
        {props.part3} {props.exercises3}
        </p>
    )
}

const Total = ({sum}) => {
    return (
        <div>
            <b>Number of exercises {sum}</b>
        </div>
    )
}

const Course = ({course}) => {
    const sum = course.parts.reduce((accu,ele) => {
        console.log(accu,ele['exercises'])
        return accu + ele["exercises"]
    })

    return (
        <div>
            <Header name={course.name}/>
            <Content parts={course.parts}/>
            <Total sum={sum}/>
        </div>
    )
}

export default Course
